mapper.insert:
		INSERT	INTO	MEMBER 	(
									EMAIL
									, NAME
									, PASSWORD
									, SALT
									, LOGIN_FAIL_COUNT
									, LAST_LOGIN
                                    , ADMIN_YN
								 )
		VALUES					 (
									#{email}
									, #{name}
									, #{password}
									, #{salt}
									, 0
									, SYSDATE
                                    , 'N'
								 )

mapper.select:

SELECT	SALT
FROM	MEMBER
WHERE	EMAIL = #{email}

SELECT	COUNT(1)
FROM	MEMBER
WHERE	EMAIL=#{email}

mapper.update:
UPDATE	MEMBER 
SET		LOGIN_FAIL_COUNT = 0
WHERE	EMAIL = #{email}
